package main

import (
	"fmt"
)

func longestPalindrome(s string) int {
	M := make(map[byte]int)
	for i := 0; i < len(s); i++ {
		M[s[i]]++
	}

	odd := 0
	sum := 0
	for _, v := range M {
		sum += v
		if v%2 == 1 {
			odd++
		}
	}
	if odd > 1 {
		sum -= odd - 1
	}

	return sum
}

func main() {
	fmt.Println(longestPalindrome(""))
}
